import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { Prospecto } from '../../Classes/Prospecto';

@Component({
  selector: 'app-home',
  templateUrl: './captura.component.html',
})
export class CapturaComponent implements OnInit {
  prospectoModel = new Prospecto("", "", "", "", 0, "", 0, "", "", [], "");
  exampleModel = new Prospecto("Lop", "Martin", "", "LOPS", 1000, "sojq", 80000, "6446644664", "1234567890123", [], "");
  counter = 0;

  constructor() { }
  @ViewChild('documentos')
    d1!: ElementRef;

  ngOnInit() {
    this.prospectoModel.documentos[0] = { nombreDocumento: "", documento: null };
    this.prospectoModel.documentos[0].nombreDocumento = "q";
  }

  agregarDoc() {
    this.d1.nativeElement.insertAdjacentHTML('beforeend', `<span>
      <input type="text" name="nombreDocumento${this.counter}" placeholder="Nombre del documento" [(ngModel)]="prospectoModel.documentos[${this.counter}].nombreDocumento"/>
      <input type="file" name="documento${this.counter}" [(ngModel)]="prospectoModel.documentos[${this.counter}].documento">
    </span><br/><br/>`);
    this.counter++;
  }

  onSubmit() {
    this.exampleModel = this.prospectoModel;
  }
}
